				if GAMESTATE:GetSongBeat()>=352 and fgcurcommand == 5 then
					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):linear(9.6);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):rotationz(12960);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):linear(9.6);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):rotationz(12960);
					end

					fgcurcommand = fgcurcommand + 1;
				end




				if GAMESTATE:GetSongBeat()>=384 and fgcurcommand == 6 then
					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('LifeP1'):linear(1.8);
						SCREENMAN:GetTopScreen():GetChild('LifeP1'):zoomy(0);
						SCREENMAN:GetTopScreen():GetChild('ScoreP1'):linear(1.8);
						SCREENMAN:GetTopScreen():GetChild('ScoreP1'):zoomx(0);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('LifeP2'):linear(1.8);
						SCREENMAN:GetTopScreen():GetChild('LifeP2'):zoomy(0);
						SCREENMAN:GetTopScreen():GetChild('ScoreP2'):linear(1.8);
						SCREENMAN:GetTopScreen():GetChild('ScoreP2'):zoomx(0);
					end

					fgcurcommand = fgcurcommand + 1;
				end




			end

			self:queuecommand('Update2');

		end
