Layer.UpdateCommand:
%function(self)

			if GAMESTATE:IsCourseMode() or (FUCK_EXE and tonumber(GAMESTATE:GetVersionDate())>=20170210) then

				if GAMESTATE:GetSongBeat()>=0 and fgcurcommand == 1 then
					SCREENMAN:GetTopScreen():GetChild('Overlay'):hidden(1);
					SCREENMAN:GetTopScreen():GetChild('Underlay'):hidden(1);

					fgcurcommand = fgcurcommand + 1;
				end




				if GAMESTATE:GetSongBeat()>=72 and fgcurcommand == 2 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectmagnitude(0,-100,0);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectmagnitude(0,-100,0);
					end

					fgcurcommand = fgcurcommand + 1;
				end




				if GAMESTATE:GetSongBeat()>=132 and fgcurcommand == 3 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):stopeffect();
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):stopeffect();
					end

					fgcurcommand = fgcurcommand + 1;
				end







				if GAMESTATE:GetSongBeat()>=136 and fgcurcommand == 4 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectmagnitude(0,-100,0);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectmagnitude(0,-100,0);
					end

					fgcurcommand = fgcurcommand + 1;
				end






				if GAMESTATE:GetSongBeat()>=164 and fgcurcommand == 5 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):stopeffect();
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):stopeffect();
					end

					fgcurcommand = fgcurcommand + 1;
				end





				if GAMESTATE:GetSongBeat()>=172 and fgcurcommand == 6 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectmagnitude(0,-100,0);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectmagnitude(0,-100,0);
					end

					fgcurcommand = fgcurcommand + 1;
				end




				if GAMESTATE:GetSongBeat()>=196 and fgcurcommand == 7 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):stopeffect();
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):stopeffect();
					end

					fgcurcommand = fgcurcommand + 1;
				end




				if GAMESTATE:GetSongBeat()>=200 and fgcurcommand == 8 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectmagnitude(0,-100,0);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectmagnitude(0,-100,0);
					end

					fgcurcommand = fgcurcommand + 1;
				end





				if GAMESTATE:GetSongBeat()>=228 and fgcurcommand == 9 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):stopeffect();
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):stopeffect();
					end

					fgcurcommand = fgcurcommand + 1;
				end






				if GAMESTATE:GetSongBeat()>=328 and fgcurcommand == 10 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):effectmagnitude(0,-100,0);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):bounce();
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectperiod(1);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectclock('bgm');
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):effectmagnitude(0,-100,0);
					end

					fgcurcommand = fgcurcommand + 1;
				end




				if GAMESTATE:GetSongBeat()>=384 and fgcurcommand == 11 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):stopeffect();
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):stopeffect();
					end

					fgcurcommand = fgcurcommand + 1;
				end



				if GAMESTATE:GetSongBeat()>=393 and fgcurcommand == 12 then

					if GAMESTATE:IsHumanPlayer(PLAYER_1) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):linear(5);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):zoomtoheight(10);
						SCREENMAN:GetTopScreen():GetChild('PlayerP1'):addy(1000);
						SCREENMAN:GetTopScreen():GetChild('LifeP1'):linear(5);
						SCREENMAN:GetTopScreen():GetChild('LifeP1'):zoomtoheight(10);
						SCREENMAN:GetTopScreen():GetChild('LifeP1'):addy(1250);
						SCREENMAN:GetTopScreen():GetChild('ScoreP1'):linear(5);
						SCREENMAN:GetTopScreen():GetChild('ScoreP1'):zoomtoheight(10);
						SCREENMAN:GetTopScreen():GetChild('ScoreP1'):addy(100);
					end
					if GAMESTATE:IsHumanPlayer(PLAYER_2) then
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):linear(5);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):zoomtoheight(10);
						SCREENMAN:GetTopScreen():GetChild('PlayerP2'):addy(1000);
						SCREENMAN:GetTopScreen():GetChild('LifeP2'):linear(5);
						SCREENMAN:GetTopScreen():GetChild('LifeP2'):zoomtoheight(10);
						SCREENMAN:GetTopScreen():GetChild('LifeP2'):addy(1250);
						SCREENMAN:GetTopScreen():GetChild('ScoreP2'):linear(5);
						SCREENMAN:GetTopScreen():GetChild('ScoreP2'):zoomtoheight(10);
						SCREENMAN:GetTopScreen():GetChild('ScoreP2'):addy(100);
					end

					fgcurcommand = fgcurcommand + 1;
				end






			end

			self:queuecommand('Update2');

		end
